mapper.select:
SELECT UPLOAD_NO, TITLE, CONTENT, CREATE_DATE, MODIFY_DATE
  FROM UPLOAD
 WHERE UPLOAD_NO = #{uploadNo}

SELECT ATTACH_NO, PATH, ORIGIN, FILESYSTEM, DOWNLOAD_CNT, UPLOAD_NO
  FROM ATTACH
 WHERE UPLOAD_NO = #{uploadNo}

mapper.update:
UPDATE ATTACH
	SET DOWNLOAD_CNT = DOWNLOAD_CNT + 1
	WHERE ATTACH_NO = #{attachNo}

mapper.select:
SELECT ATTACH_NO, PATH, ORIGIN, FILESYSTEM, DOWNLOAD_CNT, UPLOAD_NO
 FROM ATTACH
WHERE ATTACH_NO = #{attachNo}
